using Microsoft.VisualStudio.TestTools.UnitTesting;
using AdventOfCode;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace AdventOfCode.Tests
{
    [TestClass()]
    public class Day02Tests
    {
        [TestMethod()]
        public void SolveKeypadTest()
        {
            var input = new List<string> {"ULL",
                                          "RRDDD",
                                          "LURDL",
                                          "UUUUD" };
            Assert.AreEqual("1985", Day02.SolveKeypad(input));
        }
        [TestMethod()]
        public void SolveKeypadTest1()
        {
            var input = new List<string> {
                                        "",
                                        "",
                                        "",
                                        "",
                                        ""
            };
            Assert.AreEqual("99332", Day02.SolveKeypad(input));
        }

        [TestMethod()]
        public void SolveWeirdKeypadTest()
        {
            var input = new List<string> {"ULL",
                                          "RRDDD",
                                          "LURDL",
                                          "UUUUD" };
            Assert.AreEqual("5DB3", Day02.SolveWeirdKeypad(input));
        }

        [TestMethod()]
        public void SolveWeirdKeypadTest2()
        {
            var input = new List<string> {
                                        "",
                                        "",
                                        "",
                                        "",
                                        ""
            };
            Assert.AreEqual("DD483", Day02.SolveWeirdKeypad(input));
        }
    }
}